-- ----------------------------
-- Table structure for m_page
-- ----------------------------
DROP TABLE IF EXISTS `m_page`;
CREATE TABLE `m_page`  (
  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT,
  `category_id` int(10) UNSIGNED NOT NULL COMMENT '分类ID',
  `title` varchar(200) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '标题',
  `description` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '摘要',
  `content` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '内容',
  `image_url` varchar(200) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '图片',
  `create_time` datetime NULL DEFAULT NULL COMMENT '添加时间',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `category_id`(`category_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 3 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Compact;

-- ----------------------------
-- Records of m_page
-- ----------------------------
INSERT INTO `m_page` VALUES (1, 5, '关于我', '大家好，欢迎来到我的博客！本人PHP程序员一枚，因为对PHP和Web前端有着比较浓厚的兴趣，所以现在从事着PHP开发的工作。', '<p><br></p>', '', '2018-04-20 13:48:52');
INSERT INTO `m_page` VALUES (2, 6, '关于machelTony', '这个博客是用ThinkPHP+layui做的，简单方便，没有复杂的功能，就是一个简单博客系统。', '<p style=\"text-align: center;\"><br></p><p>这个博客是用ThinkPHP+layui做的，简单方便，没有复杂的功能，就是一个简单博客系统。</p>功能:<br>1、内容模型有:单页模型、文章模型、链接模型、图集模型、下载模型。<br>2、留言功能(暂时不可回复)<br>3、文章评论(集成了搜狐的畅言评论，需后台配置)<br><br>', '', '2018-04-23 11:29:24');

-- ----------------------------
-- Table structure for m_picture
-- ----------------------------
DROP TABLE IF EXISTS `m_picture`;
CREATE TABLE `m_picture`  (
  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT,
  `category_id` int(10) UNSIGNED NOT NULL COMMENT '栏目ID',
  `title` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '图片名称',
  `image_url` varchar(200) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '' COMMENT '封面图片',
  `images` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '图集地址json',
  `content` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '详情',
  `description` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '图片描述',
  `is_recommend` tinyint(1) NULL DEFAULT 0 COMMENT '是否推荐',
  `create_time` datetime NULL DEFAULT NULL COMMENT '添加时间',
  `hits` int(11) NULL DEFAULT NULL COMMENT '点击量',
  `url` varchar(200) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '地址',
  `is_top` tinyint(4) NULL DEFAULT 0 COMMENT '是否置顶',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `category_id`(`category_id`) USING BTREE,
  INDEX `is_recommend`(`is_recommend`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 3 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Compact;

